trilit_alphabet={
"A": "AAA", "B": "AAB", "C": "AAC", "D": "ABA", "E": "ABB", "F": "ABC", "G": "ACA", "H": "ACB", "I": "ACC", "J": "BAA", "K": "BAB", "L": "BAC", "M": "BBA",   "N": "BBB", "O": "BBC", "P": "BCA", "Q": "BCB", "R": "BCC", "S": "CAA", "T": "CAB", "U": "CAC", "V": "CBA",  "W": "CBB", "X": "CBC", "Y": "CCA", "Z": "CCB"
}

decode_alphabet = {v: k for k, v in trilit_alphabet.items()}

def decode(encoded_message):
    trilit_letters=[encoded_message[i:i+3] for i in range(0, len(encoded_message), 3)]
    decoded_message=[]
    for triad in trilit_letters:
        letter = decode_alphabet[triad]
        decoded_message.append(letter)
    return ''.join(decoded_message)

def calculate_letter_frequency(text):
    filtered_text = ''.join(filter(str.isalpha, text)).lower()
    frequency = Counter(filtered_text)
    return dict(sorted(frequency.items(), key=lambda item: item[1], reverse=True))

if __name__ == "__main__":

    _text=text.replace(' ','')
    _message=_text.replace('|', 'B').replace('\\','A').replace('/','C')
    decoded_message = decode(_message)
    frequencies = calculate_letter_frequency(decoded_message)

    print(str(decode_alphabet).replace("'","").replace(": ","="))
    print(f' --- {decoded_message}')
    print(f'{len(decoded_message)} {len(decoded_message)/3}')
    print(str(frequencies).replace("'","").replace(": ","="))
